package com.programming.batch1;

import java.util.Arrays;

public class SortedArrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] intArr = {10,9,67,56,98,3,1,2};
		Arrays.sort(intArr);
		System.out.println("Sorted Array is  " + Arrays.toString(intArr));
		
		String[] strArr = {"R","S","I","H","W","O","A"};
		Arrays.sort(strArr);
		System.out.println("Sorted Array is " + Arrays.toString(strArr));

	}

}
